import React, { useState, useEffect } from "react";
import "./App.css";
import {
  MenuItem,
  FormControl,
  Select,
  Card,
  CardContent,
} from "@material-ui/core";
import Table from "./components/Table";
import InfoBox from "./components/InfoBox";
import Leftpanel from "./components/Leftpanel";
import Population from "./components/Population";
import Statewise from "./components/Statewise";

function App() {
  const [country, setCountry] = useState("worldwide");
  const [countries, setCountries] = useState([]);
  const [countryInfo, setCountryInfo] = useState({});
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
      .then((response) => response.json())
      .then((data) => {
        setCountryInfo(data);
      });
  }, []);

  useEffect(() => {
    const getCountriesData = async () => {
      fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
          const countries = data.map((country) => ({
            name: country.country,
            value: country.countryInfo.iso2,
          }));
          setTableData(data);
          setCountries(countries);
        });
    };

    getCountriesData();
  }, []);
  const [data, setData] = useState({});

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/states")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setData(data);
      });
  }, []);
  const onCountryChange = async (e) => {
    const countryCode = e.target.value;

    const url =
      countryCode === "worldwide"
        ? "https://disease.sh/v3/covid-19/all"
        : `https://disease.sh/v3/covid-19/countries/${countryCode}`;
    await fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setCountry(countryCode);
        setCountryInfo(data);
        setData(data);
      });
  };
  return (
    <>
      <div className="leftpanel">
        <Leftpanel />
      </div>
      <div className="rightpanel">
        <div className="country-style">
          <div className="app__header">
            <h1>COVID-19 Tracker</h1>
            <FormControl className="app__dropdown">
              <Select
                variant="outlined"
                value={country}
                onChange={onCountryChange}
              >
                <MenuItem value="worldwide">Worldwide</MenuItem>
                {countries.map((country) => (
                  <MenuItem value={country.value}>{country.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </div>
        </div>
        <div className="card-style">
          <InfoBox
            title="Coronavirus Cases"
            cases={countryInfo.todayRecovered}
            total={4000}
          />
          <InfoBox
            title="recovered"
            cases={countryInfo.population}
            total={4000}
          />
          <InfoBox title="death Cases" cases={124} total={4000} />
          <InfoBox title="death Cases" cases={124} total={4000} />
        </div>
        {/* <Card>
        <CardContent>
          <Table countries={tableData} />
        </CardContent>
      </Card> */}
        {/* to check the polation and otheer table */}
        <div className="population">
          <Population title="try" population="120" />
          <Population title="death Cases" population={countryInfo.population} />
          <Population title="death Cases" population={countryInfo.population} />
          <Population title="death Cases" population={countryInfo.population} />
          <Population title="death Cases" population={countryInfo.population} />
        </div>
      </div>
      <Statewise />
    </>
  );
}

export default App;
